import React, { Component, Fragment } from "react";
import axios from "axios";
import { Table } from "react-bootstrap";

class DogList extends Component {
	constructor() {
		super();
		this.state = {
			dogs: []
		};
	}
	componentWillMount() {
		axios.get("/dogs").then(res => {
			this.setState({ dogs: res.data });
		});
	}

	render() {
		const handleDelete = (e, id) => {
			axios.delete("/dogs/" + id).then(res => console.log(res));
		};
		return (
			<Fragment>
				<div className="container">
					<Table striped bordered hover variant="dark">
						<thead>
							<tr>
								<th>#</th>
								<th>Dog Name</th>
								<th>Dog Owner</th>
								<th>Notes</th>
								<th>Edit</th>
								<th>Delete</th>
							</tr>
						</thead>
						<tbody>
							{this.state.dogs.map(dog => {
								return (
									<tr key={dog.id}>
										<td>{dog.id}</td>
										<td>{dog.name}</td>
										<td>{dog.owner}</td>
										<td>{dog.notes}</td>
										<td>
											<a className="btn btn-primary" href={"/dogs/" + dog.id}>
												Edit
											</a>
										</td>
										<td>
											<a
												className="btn btn-danger"
												href="#"
												onClick={() => handleDelete(dog.id)}
											>
												Delete
											</a>
										</td>
									</tr>
								);
							})}
						</tbody>
					</Table>
				</div>
			</Fragment>
		);
	}
}

export default DogList;
