import React, { Component, Fragment } from "react";
import axios from "axios";
import { Table } from "react-bootstrap";
import { Modal, Button, Form, Alert } from "react-bootstrap";

class DogList extends Component {
	constructor() {
		super();
		this.state = {
			dogs: [],
			show: false,
			id: "",
			name: "",
			owner: "",
			notes: "",
			showAlert: false,
			alertType: "success"
		};
	}

	fetchData = () => {
		axios.get("/dogs").then(res => {
			this.setState({ dogs: res.data });
		});
	};
	componentDidMount() {
		this.fetchData();
	}

	render() {
		const handleDelete = id => {
			axios
				.delete("/dogs/" + id)
				.then(res => console.log(res))
				.catch(e => console.log(e));
			this.fetchData();
		};

		const handleEdit = dog => {
			let { id, name, owner, notes } = dog;
			this.setState({
				show: true,
				id: id,
				name: name,
				owner: owner,
				notes: notes
			});
		};

		const handleHide = () => {
			this.setState({ show: false });
		};
		const handleChange = e => {
			const { name, value } = e.target;
			this.setState({
				[name]: value
			});
		};
		const handleUpdate = () => {
			let { id, name, owner, notes } = this.state;
			let json = {
				id: id,
				name: name,
				owner: owner,
				notes: notes
			};
			axios
				.put("/dogs/" + id, json)
				.then(res => {
					this.componentDidMount();
					res.data.message &&
						this.setState({ alertType: "success", showAlert: true });
					res.data.error &&
						this.setState({ alertType: "dander", showAlert: true });
					this.fetchData();
				})
				.catch(e => console.log(e));
			setTimeout(() => {
				this.setState({ showAlert: false });
			}, 3000);
			handleHide();
		};

		return (
			<Fragment>
				<div className="container">
					<Table striped bordered hover variant="dark">
						<thead>
							<tr>
								<th>#</th>
								<th>Dog Name</th>
								<th>Dog Owner</th>
								<th>Notes</th>
								<th>Edit</th>
								<th>Delete</th>
							</tr>
						</thead>
						<tbody>
							{this.state.dogs.map(dog => {
								return (
									<tr key={dog.id}>
										<td>{dog.id}</td>
										<td>{dog.name}</td>
										<td>{dog.owner}</td>
										<td>{dog.notes}</td>
										<td>
											<a
												className="btn btn-primary"
												href="#"
												onClick={() => handleEdit(dog)}
											>
												Edit
											</a>
										</td>
										<td>
											<a
												className="btn btn-danger"
												href="#"
												onClick={() => handleDelete(dog.id)}
											>
												Delete
											</a>
										</td>
									</tr>
								);
							})}
						</tbody>
					</Table>
				</div>

				<Modal
					show={this.state.show}
					onHide={handleHide}
					dialogClassName="modal-90w"
				>
					<Modal.Header closeButton>
						<Modal.Title>Edit Dog Information</Modal.Title>
					</Modal.Header>

					<Modal.Body>
						<Form>
							<Form.Group controlId="name">
								<Form.Label>Dog Name</Form.Label>
								<Form.Control
									type="text"
									value={this.state.name}
									onChange={handleChange}
									name="name"
								/>
							</Form.Group>

							<Form.Group controlId="owner">
								<Form.Label>Dog Owner</Form.Label>
								<Form.Control
									type="text"
									value={this.state.owner}
									onChange={handleChange}
									name="owner"
								/>
							</Form.Group>
							<Form.Group controlId="notes">
								<Form.Label>Notes</Form.Label>
								<Form.Control
									type="text"
									value={this.state.notes}
									onChange={handleChange}
									name="notes"
								/>
							</Form.Group>
						</Form>
					</Modal.Body>

					<Modal.Footer>
						<Button variant="secondary">Close</Button>
						<Button variant="primary" onClick={handleUpdate}>
							Update
						</Button>
						<Button
							onClick={() => handleDelete(this.state.id)}
							variant="danger"
						>
							Delete
						</Button>
					</Modal.Footer>
				</Modal>

				<Alert variant={this.state.alertType} show={this.state.showAlert}>
					{this.state.alertType == "error"
						? "Something went wrong!"
						: "Success"}
				</Alert>
			</Fragment>
		);
	}
}

export default DogList;
